// filepath: /Users/n0096336/Downloads/FullStack/capstone2/customer-management-app/src/services/customerService.js
// LocalStorage key for customer data persistence
const STORAGE_KEY = 'customer-management-data';

// Helper to check if we've already initialized localStorage
const isInitialized = () => {
    return localStorage.getItem(STORAGE_KEY) !== null;
};

// Initialize from JSON file if needed
const initializeFromJson = async () => {
    try {
        console.log('Initializing from JSON file...');
        const response = await fetch('./data/customers.json');

        if (!response.ok) {
            console.error(`Error fetching JSON: ${response.status} ${response.statusText}`);
            throw new Error(`HTTP error! status: ${response.status}`);
        }

        const initialData = await response.json();
        console.log('Initial data loaded:', initialData);
        localStorage.setItem(STORAGE_KEY, JSON.stringify(initialData));
        return initialData;
    } catch (error) {
        console.error("Failed to load initial data:", error);
        // Fallback to hardcoded data if JSON file can't be loaded
        const fallbackData = [
            { id: 1, name: 'Mike Johnson', email: 'mikej@abc.com', password: 'mikej' },
            { id: 2, name: 'Cindy Smith', email: 'cinds@abc.com', password: 'cinds' },
            { id: 3, name: 'Julio Martin', email: 'julim@abc.com', password: 'julim' }
        ];
        console.log('Using fallback data:', fallbackData);
        localStorage.setItem(STORAGE_KEY, JSON.stringify(fallbackData));
        return fallbackData;
    }
};

export const getCustomers = async () => {
    try {
        // If not initialized, load from JSON file
        if (!isInitialized()) {
            return await initializeFromJson();
        }

        // Otherwise get from localStorage
        const data = localStorage.getItem(STORAGE_KEY);
        return JSON.parse(data) || [];
    } catch (error) {
        console.error("Failed to fetch customers:", error);
        return []; // Return empty array on error
    }
};

export const addCustomer = async (customer) => {
    try {
        // Get current customers
        const currentCustomers = await getCustomers();

        // Generate a new ID (max + 1)
        const nextId = currentCustomers.length > 0 
            ? Math.max(...currentCustomers.map(c => c.id)) + 1 
            : 1;

        // Create new customer with ID
        const newCustomer = { 
            id: nextId,
            ...customer 
        };

        // Add to list and save
        const updatedCustomers = [...currentCustomers, newCustomer];
        localStorage.setItem(STORAGE_KEY, JSON.stringify(updatedCustomers));

        return newCustomer;
    } catch (error) {
        console.error("Failed to add customer:", error);
        throw error;
    }
};

export const updateCustomer = async (id, updatedData) => {
    try {
        const customers = await getCustomers();
        const index = customers.findIndex(c => c.id === id);

        if (index === -1) {
            throw new Error(`Customer with ID ${id} not found`);
        }

        // Update the customer
        const updatedCustomer = { 
            ...customers[index], 
            ...updatedData 
        };
        customers[index] = updatedCustomer;

        // Save to localStorage
        localStorage.setItem(STORAGE_KEY, JSON.stringify(customers));

        return updatedCustomer;
    } catch (error) {
        console.error(`Failed to update customer ${id}:`, error);
        throw error;
    }
};

export const deleteCustomer = async (id) => {
    try {
        const customers = await getCustomers();
        const filteredCustomers = customers.filter(c => c.id !== id);

        if (filteredCustomers.length === customers.length) {
            throw new Error(`Customer with ID ${id} not found`);
        }

        // Save filtered list
        localStorage.setItem(STORAGE_KEY, JSON.stringify(filteredCustomers));

        return { message: 'Customer deleted successfully' };
    } catch (error) {
        console.error(`Failed to delete customer ${id}:`, error);
        throw error;
    }
};
