import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { getCustomers } from '../services/customerService';

const CustomerDetail = ({ customer: propCustomer }) => {
    const { id } = useParams();
    const [customer, setCustomer] = useState(propCustomer || null);
    const [loading, setLoading] = useState(!propCustomer);

    useEffect(() => {
        // If we have an ID from URL params but no customer prop, fetch the customer
        if (id && !propCustomer) {
            const fetchCustomer = async () => {
                try {
                    const customers = await getCustomers();
                    const foundCustomer = customers.find(c => c.id === parseInt(id));
                    setCustomer(foundCustomer);
                } catch (error) {
                    console.error('Error fetching customer:', error);
                } finally {
                    setLoading(false);
                }
            };
            fetchCustomer();
        } else if (propCustomer) {
            setCustomer(propCustomer);
            setLoading(false);
        }
    }, [id, propCustomer]);

    if (loading) {
        return <div className="loading">Loading customer...</div>;
    }

    if (!customer) {
        return (
            <div className="customer-detail">
                <h2>Customer Details</h2>
                {id ? (
                    <>
                        <p>No customer found with ID: {id}</p>
                        <Link to="/" className="back-link">← Back to Customer List</Link>
                    </>
                ) : (
                    <p>Select a customer to see the details.</p>
                )}
            </div>
        );
    }

    return (
        <div className="customer-detail">
            {id && <Link to="/" className="back-link">← Back to Customer List</Link>}
            <h2>Customer Details</h2>
            <div className="customer-info">
                <div className="info-group">
                    <label>ID:</label>
                    <span>{customer.id}</span>
                </div>
                <div className="info-group">
                    <label>Name:</label>
                    <span>{customer.name}</span>
                </div>
                <div className="info-group">
                    <label>Email:</label>
                    <span>{customer.email}</span>
                </div>
                <div className="info-group">
                    <label>Password:</label>
                    <span>{"*".repeat(customer.password.length)}</span>
                </div>
            </div>
        </div>
    );
};

export default CustomerDetail;
